pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

import {IERC5192} from "./interfaces/IERC5192.sol";

abstract contract ERC5192 is ERC721, IERC5192 {
  bool private isLocked;

  error ErrLocked();
  error ErrNotFound();

  constructor(string memory _name, string memory _symbol, bool _isLocked)
    ERC721(_name, _symbol)
  {
    isLocked = _isLocked;
  }

  modifier checkLock() {
    if (isLocked) revert ErrLocked();
    _;
  }

  function locked(uint256 tokenId) external view returns (bool) {
    if (_ownerOf(tokenId) != address(0)) revert ErrNotFound();
    return isLocked;
  }

  function safeTransferFrom(
    address from,
    address to,
    uint256 tokenId,
    bytes memory data
  ) public override checkLock {
    super.safeTransferFrom(from, to, tokenId, data);
  }

/*
  function safeTransferFrom(address from, address to, uint256 tokenId)
    public
    override
    checkLock
  {
    super.safeTransferFrom(from, to, tokenId);
  }
*/
  function transferFrom(address from, address to, uint256 tokenId)
    public
    override
    checkLock
  {
    super.transferFrom(from, to, tokenId);
  }

  function approve(address approved, uint256 tokenId) public override checkLock {
    super.approve(approved, tokenId);
  }

  function setApprovalForAll(address operator, bool approved)
    public
    override
    checkLock
  {
    super.setApprovalForAll(operator, approved);
  }

  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override
    returns (bool)
  {
    return interfaceId == type(IERC5192).interfaceId
      || super.supportsInterface(interfaceId);
  }
}","keccak256":"0xb68e68ce3daf6805fdf1e608c372700d66966471af3e0af6eaaf6b6d3ede4a64","license":"GPL-3.0-only"},"contracts/SBT.sol":{"content":"// SPDX-License-Identifier: MIT 
pragma solidity >=0.8.0 <0.9.0;

import {ERC5192} from "./ERC5192.sol";
import {ISBT} from "./interfaces/ISBT.sol";

/**
 * @title SBT - Soulbound Token
 * @notice Implementazione di un token soulbound per E-Voting (non trasferibile) basato su ERC5192
 * @dev Estende ERC5192 per creare token legati permanentemente a un indirizzo
 * @author exo404, simonemontella, valeriooconte
 */
contract SBT is ERC5192, ISBT {

    uint256 public nextSBTId = 1;

    /**
     * @notice Mapping che associa ogni indirizzo all'ID del suo SBT
     * @dev Restituisce 0 se l'indirizzo non possiede alcun SBT
     */
    mapping(address => uint256) public hasSBT;

    /**
     * @notice Mapping che associa ogni indirizzo ai dati del suo SBT
     * @dev Contiene le informazioni personali associate al token
     */
    mapping(address => SBTData) public sbts;

    /**
     * @notice Costruttore del contratto SBT
     * @dev Inizializza il contratto padre ERC5192 con i parametri forniti
     */
    constructor() ERC5192("3VT SBT", "3VSBT", true) {}

    /// @inheritdoc ISBT
    function createSBT(SBTData memory _data) external {

        if (hasSBT[msg.sender] != 0) revert AlreadyOwned();
        if (bytes(_data.name).length == 0 || bytes(_data.surname).length == 0) {
            revert InvalidData();
        }

        _data.issueDate = block.timestamp;

        sbts[msg.sender] = _data;
        hasSBT[msg.sender] = nextSBTId;

        _safeMint(msg.sender, nextSBTId);
        emit SBTCreated(msg.sender, nextSBTId, _data.issueDate);
        nextSBTId++;
    }


    /// @inheritdoc ISBT
    function getSBTData(address owner) external view returns (SBTData memory) {
        return sbts[owner];
    }

    /// @inheritdoc ISBT
    function totalSupply() external view returns (uint256) {
        return nextSBTId - 1;
    }
}","keccak256":"0xa680ecacd0d5c40c36c3339944602f264378fc0686a37c92556c1f5ea53ea5a4","license":"MIT"},"contracts/interfaces/IERC5192.sol":{"content":"// SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.20;

interface IERC5192 {
  /// @notice Emitted when the locking status is changed to locked.
  /// @dev If a token is minted and the status is locked, this event should be emitted.
  /// @param tokenId The identifier for a token.
  event Locked(uint256 tokenId);

  /// @notice Emitted when the locking status is changed to unlocked.
  /// @dev If a token is minted and the status is unlocked, this event should be emitted.
  /// @param tokenId The identifier for a token.
  event Unlocked(uint256 tokenId);

  /// @notice Returns the locking status of an Soulbound Token
  /// @dev SBTs assigned to zero address are considered invalid, and queries
  /// about them do throw.
  /// @param tokenId The identifier for an SBT.
  function locked(uint256 tokenId) external view returns (bool);
}","keccak256":"0xab6074248b7ec8ebd9b1de6f3a9da9aa18217881ece69440dee75f9e6fb714d7","license":"CC0-1.0"},"contracts/interfaces/ISBT.sol":{"content":"// SPDX-License-Identifier: MIT 
pragma solidity >=0.8.20 <0.9.0;

import {ERC5192} from "../ERC5192.sol";

/**
 * @title ISBT 
 * @notice Interfaccia di un token soulbound per E-Voting (non trasferibile) basato su ERC5192
 * @author exo404, simonemontella, valeriooconte
 */
interface ISBT {

    ///////////////////// DATA STRUCTURES /////////////////////

    /**
     * @notice Struttura dati per memorizzare le informazioni di un SBT
     * @param issueDate Data di emissione del token (timestamp)
     * @param name Nome del possessore del token
     * @param surname Cognome del possessore del token
     * @param birthDate Data di nascita del possessore (timestamp)
     * @param birthPlace Luogo di nascita del possessore
     */
    struct SBTData {
        uint256 issueDate;
        string name;
        string surname;
        uint256 birthDate;
        string birthPlace;
    }

    ///////////////////// EVENTS /////////////////////

    /**
     * @notice Evento emesso quando viene creato un nuovo SBT
     * @param owner Indirizzo del proprietario del nuovo SBT
     * @param tokenId ID del token creato
     * @param issueDate Data di emissione del token
     */
    event SBTCreated(address indexed owner, uint256 indexed tokenId, uint256 issueDate);

    ///////////////////// ERRORS /////////////////////

    /**
     * @notice Errore emesso quando un indirizzo tenta di creare un SBT ma ne possiede già uno
     */
    error AlreadyOwned();

    /**
     * @notice Errore emesso quando si tenta di creare un SBT con dati vuoti
     */
    error InvalidData();

    ///////////////////// FUNCTIONS /////////////////////

    /**
     * @notice Crea un nuovo SBT con i dati forniti per il mittente della transazione
     * @param _data Struttura SBTData contenente le informazioni del token (issueDate verrà sovrascritto)
     * @dev Reverte con AlreadyOwned se il mittente possiede già un SBT
     * @dev Reverte con InvalidData se i dati essenziali sono vuoti
     * @dev Crea un token nel contratto corrente, memorizza i dati e incrementa il contatore
     * @dev Aggiunge automaticamente la data di emissione al momento della creazione
     */
    function createSBT(SBTData memory _data) external;

    /**
     * @notice Restituisce i dati completi di un SBT dato l'indirizzo del proprietario
     * @param owner Indirizzo del proprietario del SBT
     * @return SBTData Struttura contenente tutti i dati del SBT
     * @dev Restituisce una struttura vuota se l'indirizzo non possiede alcun SBT
     */
    function getSBTData(address owner) external view returns (SBTData memory);

    /**
     * @notice Restituisce il numero totale di SBT emessi
     * @return uint256 Numero totale di SBT creati
     */
    function totalSupply() external view returns (uint256);
}","keccak256":"0x18db0000d6c5c27903546f68f938d89efcc284f0b4e522951f6e92b31e88c646","license":"MIT"}},"version":1}
